import { User } from './../../../model/user';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/service/user-service.service';

@Component({
  selector: 'app-listar-user',
  templateUrl: './listar-user.component.html',
  styleUrls: ['./listar-user.component.scss']
})
export class ListarUserComponent implements OnInit{

 listarUser: User[] = [];


constructor(
  private service: UserService,
  private router: Router
){}


ngOnInit(): void {
  this.buscaUser()
}

buscaUser(): void{
  this.service.buscar()
    .subscribe(users => this.listarUser = users )
}

deletarUser(id: any): void{
  this.service.deletar(parseInt(id!))
}

}
<div>
  <table>
    <tr>
      <th>Nome</th>
      <th>idade</th>
      <th>Data de nascimento</th>
      <th>Telefone</th>
      <th>Endereço</th>
      <th>Action</th>
    </tr>
    <tr *ngFor="let user of listarUser">
      <td>{{ user.name }}</td>
      <td>{{ user.age }}</td>
      <td>{{ user.birthday }}</td>
      <td>{{ user.phoneNumber }}</td>
      <td>{{ user.address }}</td>
      <td>
        <button routerLink="/criarUsers/{{user.id}}">Visualizar</button>

        <button (click)="deletarUser( user.id )" >Deletar</button>

      </td>
    </tr>
  </table>
  <button id="cad" routerLink="/criarUsers">Cadastrar</button>
</div>
