import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { filter } from 'rxjs';
import { UserService } from 'src/app/service/user-service.service';
import { validatorTelefone } from './validatorTelefone'
import { User } from 'src/app/model/user';

@Component({
  selector: 'app-cadastro-user',
  templateUrl:'./cadastro-user.component.html',
  styleUrls: ['./cadastro-user.component.scss']
})
export class CadastroUserComponent implements OnInit{

 formulario = this.formBuilder.group({
      name:['',[
        Validators.required,
        Validators.minLength(2)
      ]],

      age: new FormControl<number | null>(null, { validators:[Validators.required]}),
      birthday:['', [Validators.required]],
      phoneNumber:['',[validatorTelefone]]  
      ,
      address: ['', [Validators.required]]
    })
  private edit! : boolean

  constructor(
    private service: UserService,
    private route: ActivatedRoute,
    private router: Router,
    private formBuilder: FormBuilder
  ){}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id')
    if(id){
      this.service.buscarUser(parseInt(id!)).subscribe((user: any | User) => {

        debugger
        Object.keys(user).forEach((key: string, value) =>{
          this.formulario.get(key)?.setValue(user[key])
        })
        // this.formulario.get("name")?.setValue(user.name);

        // this.formulario = this.formBuilder.group({
        //   id:[id],
        //   name:[user.name],
        //   age: [user.age],
        //   birthday:[user.birthday],
        //   phoneNumber:[user.phoneNumber],
        //   address: [user.address]
        // })
      }
    )
    this.edit = true
    }else{
      this.edit = false
    }

  }

usuario(){

  this.aniversario()
  if(!(this.edit)){
    if(this.formulario.valid){
      const user = {
    } as User;

      this.service.criar(user).subscribe(() => {
        this.router.navigate(['/listarUsers'])
      })
  }
  }else{
    if(this.formulario.valid){
      const user = {
        id: this.formulario.get("id")?.value
      } as User;
      this.service.editar(user).subscribe(() => {
        this.router.navigate(['/listarUsers'])
      })
  }
  }
}

aniversario(){

  const date = new Date();
  const data = this.formulario.controls['birthday']?.value!;
  const dataAniversario = new Date(data)

  const idade:number = date.getFullYear() - dataAniversario.getFullYear()

  this.formulario.controls['age'].setValue(idade)


}

}
<div id="cad">

  <form [formGroup]="formulario" *ngIf="formulario">

    <h1>Cadastro de Usuarios</h1>

    <label for="">Nome:</label>
    <input
      type="text"
      formControlName="name"
    >

    <div class="erro_mensage" *ngIf="formulario.get('name')?.errors?.['required'] && formulario.get('name')?.touched ">
      Preencha essa campo para realizar o cadastro!
    </div>

    <div class="erro_mensage" *ngIf="formulario.get('name')?.errors?.['minlength'] && formulario.get('name')?.touched ">
      Digite o seu nome completo
    </div>


    <label for="">Data de nacimento:</label>
    <input
      type="date"
      formControlName="birthday"
      id="data"
    >

    <div class="erro_mensage" *ngIf="formulario.get('birthday')?.errors?.['required'] && formulario.get('birthday')?.touched ">
      Preencha essa campo para realizar o cadastro!
    </div>

    <label for="">Telefone:</label>
    <p>DDD liberados: '47', '11', '48','41'</p>
    <input
      type="text"
      formControlName="phoneNumber"
      placeholder="(47) 9 9999-9999"
    >

    <div class="erro_mensage" *ngIf="formulario.get('phoneNumber')?.errors?.['telefone'] && formulario.get('phoneNumber')?.touched ">
      Numero preenchido Invalido!!
    </div>

    <label for="">Endereço:</label>
    <input
      type="text"
      formControlName="address"
    >

    <div class="erro_mensage" *ngIf="formulario.get('address')?.errors?.['required'] && formulario.get('address')?.touched ">
      Preencha essa campo para realizar o cadastro!
    </div>

    <button (click)="usuario()">Cadastrar</button>

  </form>
</div>
